angular.io.css:
.level-1,
.level-2,
.level-3,
.level-4,
.app-toolbar a,
input,
button {
  font-family: "SimSun", "宋体";
}

code {
  font-family: "Monaco", monospace;
}

gitlab.com.css:
body {
  font-family: "SimSun", "宋体";
}

.diff-table.code,
table.code {
  font-family: "Monaco", monospace;
  font-size: 85%;
}

.monospace,
.commit-sha,
.gfm-commit,
.gfm-commit_range,
.ref-name,
.git-revision-dropdown .dropdown-content ul li a,
.mr-state-widget .label-branch,
.pipeline-number,
.file-editor .file-title,
.git-clone-holder .form-control,
.git-clone-holder .search form,
.search .git-clone-holder form {
  font-family: "Monaco", monospace;
  font-size: 85%;
}
